import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [result, setResult] = useState(null);

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append("text", text);

    const res = await axios.post("http://127.0.0.1:5000/predict", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    setResult(res.data);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üç≤ Cooking Recipe & Food Classifier</h1>

      <textarea
        placeholder="Enter recipe text..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ width: "100%", height: "100px", marginBottom: "10px" }}
      />

      <button onClick={handleSubmit} style={{ padding: "10px 20px", background: "blue", color: "white" }}>
        Predict
      </button>

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h2>Results:</h2>
          <p><b>Dish:</b> {result.dish}</p>
          <p><b>Cuisine:</b> {result.cuisine}</p>
          <p><b>Summary:</b> {result.summary}</p>
        </div>
      )}
    </div>
  );
}

export default App;
