import React, { useState, useRef, useEffect } from 'react';
import { Camera, Mic, MicOff, Upload, ChefHat, Globe, Clock, Users, Utensils, Sparkles, FileText, ImageIcon, Volume2 } from 'lucide-react';

const FlavorCraft = () => {
  const [activeTab, setActiveTab] = useState('text');
  const [recipeText, setRecipeText] = useState('');
  const [dishImage, setDishImage] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [generatedRecipe, setGeneratedRecipe] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);

  const mediaRecorderRef = useRef(null);
  const audioStreamRef = useRef(null);
  const fileInputRef = useRef(null);
  const timerRef = useRef(null);

  useEffect(() => {
    return () => {
      if (audioStreamRef.current) {
        audioStreamRef.current.getTracks().forEach(track => track.stop());
      }
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioStreamRef.current = stream;

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
      setRecordingTime(0);

      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Unable to access microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      clearInterval(timerRef.current);
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setDishImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const generateRecipe = async () => {
    if (!recipeText && !dishImage && !audioBlob) {
      alert('Please provide at least one input (text, image, or audio)');
      return;
    }

    setIsProcessing(true);

    // Simulate API processing with mock data
    await new Promise(resolve => setTimeout(resolve, 3000));

    // Mock generated recipe based on inputs
    const mockRecipe = {
      name: "Butter Chicken Masala",
      cuisine: "Indian",
      difficulty: "Medium",
      cookTime: "45 minutes",
      servings: 4,
      image: dishImage || "https://images.unsplash.com/photo-1588166524941-3bf61a9c41db?w=400&h=300&fit=crop",
      description: "A rich and creamy Indian curry featuring tender chicken in a tomato-based sauce with aromatic spices and butter.",
      ingredients: [
        "1 lb boneless chicken, cut into cubes",
        "1 cup heavy cream",
        "1 can (14 oz) crushed tomatoes",
        "2 tbsp butter",
        "1 onion, finely chopped",
        "3 cloves garlic, minced",
        "1 inch ginger, minced",
        "1 tsp garam masala",
        "1 tsp cumin powder",
        "1/2 tsp paprika",
        "Salt to taste",
        "Fresh cilantro for garnish"
      ],
      instructions: [
        "Marinate chicken with yogurt, salt, and spices for 30 minutes.",
        "Heat oil in a large pan and cook chicken until golden. Set aside.",
        "In the same pan, sauté onions until golden brown.",
        "Add garlic, ginger, and spices. Cook for 1 minute.",
        "Add crushed tomatoes and simmer for 10 minutes.",
        "Stir in cream and butter, then add cooked chicken.",
        "Simmer for 15 minutes until sauce thickens.",
        "Garnish with fresh cilantro and serve hot with rice or naan."
      ],
      nutritionTips: "Rich in protein and flavor. Serve with basmati rice and naan bread for a complete meal.",
      tags: ["Spicy", "Creamy", "Comfort Food", "Dinner"]
    };

    setGeneratedRecipe(mockRecipe);
    setIsProcessing(false);
  };

  const resetForm = () => {
    setRecipeText('');
    setDishImage(null);
    setAudioBlob(null);
    setGeneratedRecipe(null);
    setRecordingTime(0);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 via-red-50 to-pink-50">
      {/* Header */}
      <div className="bg-white shadow-lg border-b border-orange-100">
        <div className="max-w-6xl mx-auto px-6 py-4">
          <div className="flex items-center space-x-3">
            <div className="bg-gradient-to-r from-orange-500 to-red-500 p-2 rounded-xl">
              <ChefHat className="h-8 w-8 text-white" />
            </div>
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-orange-600 to-red-600 bg-clip-text text-transparent">
                FlavorCraft
              </h1>
              <p className="text-gray-600 text-sm">AI-Powered Multimodal Recipe Generator</p>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto px-6 py-8">
        {!generatedRecipe ? (
          /* Input Section */
          <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
            <div className="bg-gradient-to-r from-orange-500 to-red-500 px-6 py-4">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <Sparkles className="mr-2" />
                Create Your Recipe
              </h2>
              <p className="text-orange-100 mt-1">Upload text, image, or record audio to generate detailed recipes</p>
            </div>

            {/* Tab Navigation */}
            <div className="flex border-b border-gray-200">
              {[
                { id: 'text', label: 'Recipe Text', icon: FileText },
                { id: 'image', label: 'Dish Image', icon: ImageIcon },
                { id: 'audio', label: 'Voice Input', icon: Volume2 }
              ].map(({ id, label, icon: Icon }) => (
                <button
                  key={id}
                  onClick={() => setActiveTab(id)}
                  className={`flex-1 px-6 py-4 font-medium transition-colors flex items-center justify-center space-x-2 ${
                    activeTab === id
                      ? 'bg-orange-50 text-orange-600 border-b-2 border-orange-500'
                      : 'text-gray-600 hover:bg-gray-50'
                  }`}
                >
                  <Icon className="h-4 w-4" />
                  <span>{label}</span>
                </button>
              ))}
            </div>

            <div className="p-6">
              {/* Text Input Tab */}
              {activeTab === 'text' && (
                <div className="space-y-4">
                  <label className="block text-sm font-medium text-gray-700">
                    Enter Recipe Text or Description
                  </label>
                  <textarea
                    value={recipeText}
                    onChange={(e) => setRecipeText(e.target.value)}
                    placeholder="Paste your recipe here, or describe the dish you want to make..."
                    className="w-full h-40 px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-transparent resize-none"
                  />
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-500">
                      {recipeText.length} characters
                    </span>
                  </div>
                </div>
              )}

              {/* Image Input Tab */}
              {activeTab === 'image' && (
                <div className="space-y-4">
                  <label className="block text-sm font-medium text-gray-700">
                    Upload Dish Image
                  </label>
                  <div 
                    onClick={() => fileInputRef.current?.click()}
                    className="border-2 border-dashed border-gray-300 rounded-xl p-8 text-center hover:border-orange-400 transition-colors cursor-pointer"
                  >
                    {dishImage ? (
                      <div className="space-y-4">
                        <img 
                          src={dishImage} 
                          alt="Uploaded dish" 
                          className="max-h-64 mx-auto rounded-lg shadow-lg"
                        />
                        <p className="text-green-600 font-medium">Image uploaded successfully!</p>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        <Camera className="h-12 w-12 text-gray-400 mx-auto" />
                        <div>
                          <p className="text-lg font-medium text-gray-900">Upload a dish image</p>
                          <p className="text-gray-500">PNG, JPG, GIF up to 10MB</p>
                        </div>
                      </div>
                    )}
                  </div>
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="hidden"
                  />
                </div>
              )}

              {/* Audio Input Tab */}
              {activeTab === 'audio' && (
                <div className="space-y-6">
                  <label className="block text-sm font-medium text-gray-700">
                    Record Voice Instructions
                  </label>

                  <div className="text-center space-y-4">
                    {!isRecording ? (
                      <button
                        onClick={startRecording}
                        className="bg-gradient-to-r from-red-500 to-red-600 text-white px-8 py-4 rounded-full hover:from-red-600 hover:to-red-700 transition-all transform hover:scale-105 flex items-center space-x-2 mx-auto"
                      >
                        <Mic className="h-5 w-5" />
                        <span>Start Recording</span>
                      </button>
                    ) : (
                      <div className="space-y-4">
                        <div className="bg-red-50 rounded-xl p-6 border border-red-200">
                          <div className="flex items-center justify-center space-x-3 mb-4">
                            <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
                            <span className="text-red-700 font-medium">Recording...</span>
                          </div>
                          <div className="text-2xl font-mono text-red-600 text-center">
                            {formatTime(recordingTime)}
                          </div>
                        </div>
                        <button
                          onClick={stopRecording}
                          className="bg-gray-600 text-white px-8 py-3 rounded-full hover:bg-gray-700 transition-colors flex items-center space-x-2 mx-auto"
                        >
                          <MicOff className="h-5 w-5" />
                          <span>Stop Recording</span>
                        </button>
                      </div>
                    )}

                    {audioBlob && (
                      <div className="bg-green-50 rounded-xl p-4 border border-green-200">
                        <p className="text-green-700 font-medium">✓ Audio recorded successfully!</p>
                        <p className="text-green-600 text-sm">Duration: {formatTime(recordingTime)}</p>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Generate Button */}
              <div className="flex items-center space-x-4 mt-8">
                <button
                  onClick={generateRecipe}
                  disabled={isProcessing}
                  className="flex-1 bg-gradient-to-r from-orange-500 to-red-500 text-white py-4 rounded-xl font-semibold hover:from-orange-600 hover:to-red-600 transition-all transform hover:scale-[1.02] disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
                >
                  {isProcessing ? (
                    <>
                      <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                      <span>Generating Recipe...</span>
                    </>
                  ) : (
                    <>
                      <ChefHat className="h-5 w-5" />
                      <span>Generate Recipe</span>
                    </>
                  )}
                </button>
                <button
                  onClick={resetForm}
                  className="px-6 py-4 border border-gray-300 text-gray-700 rounded-xl hover:bg-gray-50 transition-colors"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        ) : (
          /* Generated Recipe Display */
          <div className="space-y-6">
            {/* Recipe Header */}
            <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
              <div className="relative">
                <img 
                  src={generatedRecipe.image} 
                  alt={generatedRecipe.name}
                  className="w-full h-64 object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent"></div>
                <div className="absolute bottom-4 left-6 text-white">
                  <h1 className="text-4xl font-bold mb-2">{generatedRecipe.name}</h1>
                  <div className="flex items-center space-x-4 text-sm">
                    <span className="flex items-center space-x-1">
                      <Globe className="h-4 w-4" />
                      <span>{generatedRecipe.cuisine}</span>
                    </span>
                    <span className="flex items-center space-x-1">
                      <Clock className="h-4 w-4" />
                      <span>{generatedRecipe.cookTime}</span>
                    </span>
                    <span className="flex items-center space-x-1">
                      <Users className="h-4 w-4" />
                      <span>{generatedRecipe.servings} servings</span>
                    </span>
                  </div>
                </div>
              </div>

              <div className="p-6">
                <p className="text-gray-700 text-lg leading-relaxed">{generatedRecipe.description}</p>

                <div className="flex flex-wrap gap-2 mt-4">
                  {generatedRecipe.tags.map((tag, index) => (
                    <span key={index} className="px-3 py-1 bg-orange-100 text-orange-700 rounded-full text-sm font-medium">
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
            </div>

            {/* Recipe Content */}
            <div className="grid lg:grid-cols-2 gap-6">
              {/* Ingredients */}
              <div className="bg-white rounded-2xl shadow-xl p-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-4 flex items-center">
                  <Utensils className="h-6 w-6 text-orange-500 mr-2" />
                  Ingredients
                </h2>
                <ul className="space-y-3">
                  {generatedRecipe.ingredients.map((ingredient, index) => (
                    <li key={index} className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-orange-500 rounded-full mt-2 flex-shrink-0"></div>
                      <span className="text-gray-700">{ingredient}</span>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Instructions */}
              <div className="bg-white rounded-2xl shadow-xl p-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-4 flex items-center">
                  <ChefHat className="h-6 w-6 text-orange-500 mr-2" />
                  Instructions
                </h2>
                <ol className="space-y-4">
                  {generatedRecipe.instructions.map((step, index) => (
                    <li key={index} className="flex items-start space-x-4">
                      <div className="w-8 h-8 bg-orange-500 text-white rounded-full flex items-center justify-center text-sm font-bold flex-shrink-0">
                        {index + 1}
                      </div>
                      <span className="text-gray-700 pt-1">{step}</span>
                    </li>
                  ))}
                </ol>
              </div>
            </div>

            {/* Nutrition Tips */}
            <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl p-6 border border-green-200">
              <h3 className="text-xl font-bold text-green-800 mb-2">💡 Chef's Tips</h3>
              <p className="text-green-700">{generatedRecipe.nutritionTips}</p>
            </div>

            {/* Action Buttons */}
            <div className="flex items-center space-x-4">
              <button
                onClick={resetForm}
                className="flex-1 bg-gradient-to-r from-orange-500 to-red-500 text-white py-4 rounded-xl font-semibold hover:from-orange-600 hover:to-red-600 transition-all transform hover:scale-[1.02] flex items-center justify-center space-x-2"
              >
                <Sparkles className="h-5 w-5" />
                <span>Generate Another Recipe</span>
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

