import React, { useState, useRef, useEffect } from 'react';
import { ChefHat } from 'lucide-react';

const FlavorCraft = () => {
  const [recipeText, setRecipeText] = useState('');
  const [dishImage, setDishImage] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioTranscript, setAudioTranscript] = useState('');
  const [generatedRecipe, setGeneratedRecipe] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);

  const mediaRecorderRef = useRef(null);
  const audioStreamRef = useRef(null);
  const timerRef = useRef(null);

  useEffect(() => {
    return () => {
      if (audioStreamRef.current) {
        audioStreamRef.current.getTracks().forEach(track => track.stop());
      }
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, []);

  // Audio recording
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioStreamRef.current = stream;

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) chunks.push(e.data);
      };

      mediaRecorder.onstop = async () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        stream.getTracks().forEach(track => track.stop());
        await transcribeAudio(blob);
      };

      mediaRecorder.start();
      setIsRecording(true);
      setRecordingTime(0);
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);

    } catch (err) {
      console.error("Microphone access error:", err);
      alert("Unable to access microphone. Check permissions.");
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      clearInterval(timerRef.current);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2,'0')}`;
  };

  // Image handling
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => setDishImage(event.target.result);
      reader.readAsDataURL(file);
    }
  };

  const dataURLtoFile = (dataurl, filename) => {
    const arr = dataurl.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    const u8arr = new Uint8Array(bstr.length);
    for (let i = 0; i < bstr.length; i++) u8arr[i] = bstr.charCodeAt(i);
    return new File([u8arr], filename, { type: mime });
  };

  // Transcribe audio via backend
  const transcribeAudio = async (blob) => {
    if (!blob) return;
    const formData = new FormData();
    formData.append("audio", blob, "voice.wav");

    try {
      const res = await fetch("http://localhost:5000/predict", { method: "POST", body: formData });
      const data = await res.json();
      setAudioTranscript(data.audio_transcript || "");
    } catch (err) {
      console.error("Error transcribing audio:", err);
    }
  };

  // Generate recipe
  const generateRecipe = async () => {
    if (!recipeText && !dishImage && !audioBlob) {
      alert("Provide at least one input (text, image, or audio)");
      return;
    }

    setIsProcessing(true);

    const formData = new FormData();
    if (recipeText) formData.append("text", recipeText);
    if (dishImage) formData.append("image", dataURLtoFile(dishImage, "dish.png"));
    if (audioBlob) formData.append("audio", audioBlob, "voice.wav");

    try {
      const res = await fetch("http://localhost:5000/predict", { method: "POST", body: formData });
      const data = await res.json();

      setGeneratedRecipe({
        name: "Butter Chicken Masala",
        cuisine: "Indian",
        difficulty: "Medium",
        cookTime: "45 minutes",
        servings: 4,
        image: dishImage || "https://images.unsplash.com/photo-1588166524941-3bf61a9c41db?w=400&h=300&fit=crop",
        description: data.text_summary || "A rich and creamy Indian curry...",
        imageClass: data.image_class || "Unknown",
        audioTranscript: audioTranscript
      });
    } catch (err) {
      console.error("Error generating recipe:", err);
    }

    setIsProcessing(false);
  };

  const resetForm = () => {
    setRecipeText('');
    setDishImage(null);
    setAudioBlob(null);
    setAudioTranscript('');
    setGeneratedRecipe(null);
    setRecordingTime(0);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 via-red-50 to-pink-50">
      {/* Header */}
      <div className="bg-white shadow-lg border-b border-orange-100">
        <div className="max-w-6xl mx-auto px-6 py-4 flex items-center space-x-3">
          <div className="bg-gradient-to-r from-orange-500 to-red-500 p-2 rounded-xl">
            <ChefHat className="h-8 w-8 text-white" />
          </div>
          <div>
            <h1 className="text-3xl font-bold bg-gradient-to-r from-orange-600 to-red-600 bg-clip-text text-transparent">
              FlavorCraft
            </h1>
            <p className="text-gray-600 text-sm">AI-Powered Multimodal Recipe Generator</p>
          </div>
        </div>
      </div>

      <div className="max-w-3xl mx-auto px-6 py-8">
        {!generatedRecipe ? (
          <div className="bg-white rounded-2xl shadow-xl p-6 space-y-4">
            <textarea
              placeholder="Enter recipe text..."
              className="w-full p-3 border border-gray-300 rounded-lg"
              value={recipeText}
              onChange={(e) => setRecipeText(e.target.value)}
            />

            <input type="file" accept="image/*" onChange={handleImageUpload} />

            {dishImage && <img src={dishImage} alt="Dish Preview" className="mt-4 w-full rounded-lg" />}

            <div className="flex space-x-4 mt-4">
              {!isRecording ? (
                <button onClick={startRecording} className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg">
                  Start Recording
                </button>
              ) : (
                <button onClick={stopRecording} className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg">
                  Stop Recording ({formatTime(recordingTime)})
                </button>
              )}

              <button
                onClick={generateRecipe}
                className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded-lg"
                disabled={isProcessing}
              >
                {isProcessing ? "Processing..." : "Generate Recipe"}
              </button>

              <button onClick={resetForm} className="bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded-lg">
                Reset
              </button>
            </div>

            {audioTranscript && (
              <p className="mt-2 text-gray-700"><strong>Transcript:</strong> {audioTranscript}</p>
            )}
          </div>
        ) : (
          <div className="bg-white rounded-2xl shadow-xl p-6 space-y-4">
            <h2 className="text-xl font-bold">{generatedRecipe.name}</h2>
            <img src={generatedRecipe.image} alt={generatedRecipe.name} className="w-full rounded-lg" />
            <p><strong>Cuisine:</strong> {generatedRecipe.cuisine}</p>
            <p><strong>Difficulty:</strong> {generatedRecipe.difficulty}</p>
            <p><strong>Cook Time:</strong> {generatedRecipe.cookTime}</p>
            <p><strong>Servings:</strong> {generatedRecipe.servings}</p>
            <p className="mt-2">{generatedRecipe.description}</p>
            <p><strong>Image Class:</strong> {generatedRecipe.imageClass}</p>
            <p><strong>Audio Transcript:</strong> {generatedRecipe.audioTranscript || "No audio input"}</p>

            <button onClick={resetForm} className="mt-4 bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded-lg">
              Back
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FlavorCraft;
