import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [image, setImage] = useState(null);
  const [audio, setAudio] = useState(null);
  const [result, setResult] = useState(null);

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append("text", text);
    if (image) formData.append("image", image);
    if (audio) formData.append("audio", audio);

    const res = await axios.post("http://127.0.0.1:5000/predict", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    setResult(res.data);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üç≤ Cooking Recipe & Food Classifier</h1>

      {/* Text Input */}
      <textarea
        placeholder="Enter recipe text..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ width: "100%", height: "100px", marginBottom: "10px" }}
      />

      {/* Image Upload */}
      <input
        type="file"
        accept="image/*"
        onChange={(e) => setImage(e.target.files[0])}
      />

      {/* Audio Upload */}
      <input
        type="file"
        accept="audio/*"
        onChange={(e) => setAudio(e.target.files[0])}
      />

      {/* Submit */}
      <button
        onClick={handleSubmit}
        style={{ marginTop: "10px", padding: "10px", background: "blue", color: "white" }}
      >
        Predict
      </button>

      {/* Results */}
      {result && (
        <div style={{ marginTop: "20px" }}>
          <h2>Results:</h2>
          <p><b>Dish:</b> {result.dish}</p>
          <p><b>Cuisine:</b> {result.cuisine}</p>
          <p><b>Summary:</b> {result.summary}</p>
        </div>
      )}
    </div>
  );
}

export default App;
